import type EntityIssue from './entity-issue.js';
import type FieldIssue from './field-issue.js';
import type EnumIssue from './enum-issue.js';
import type RelationshipIssue from './relationship-issue.js';
export default class Issues {
    entityIssues: EntityIssue[];
    fieldIssues: FieldIssue[];
    enumIssues: EnumIssue[];
    relationshipIssues: RelationshipIssue[];
    constructor();
    addEntityIssues(issues?: EntityIssue[]): void;
    addFieldIssues(issues?: FieldIssue[]): void;
    addEnumIssues(issues?: EnumIssue[]): void;
    addRelationshipIssues(issues?: RelationshipIssue[]): void;
    getNumberOfIssues(): number;
    getNumberOfEntityIssues(): number;
    getNumberOfFieldIssues(): number;
    getNumberOfEnumIssues(): number;
    getNumberOfRelationshipIssues(): number;
    getIssues(): {
        entities: EntityIssue[];
        enums: EnumIssue[];
        fields: FieldIssue[];
        relationships: RelationshipIssue[];
    };
}
